package main

import "fmt"

func santa(str string) int {
    start := 0
	pos := 1
	for _, val := range str {
		switch string(val) {
		case ")":
			start--
			break
		case "(":
			start++
			break
		}
		fmt.Println(start)
		if start == -1 {
			return pos
		}
		pos++
	}
	return start
}

